package helper.strategies.target;

import helper.Bot;
import robocode.HitRobotEvent;
import robots.BaseBot;

public abstract class TargetStrategy {
	
	/**
	 * Use robot.setTarget() to communicate your target choice to the robot.
	 * @param bot
	 */
	public abstract Bot execute(BaseBot robot);	
	
	/**
	 * This method is called when another robot collides with us.
	 * You might want to adjust your target selection then.
	 * @param robot, Our robot.
	 * @param event, the Hit RobotEvent generated by Robocode.
	 */
	public void rammingTarget(BaseBot robot, HitRobotEvent event) {		
	}
	
	@Override
	public String toString() {		
		return "Target Strategy: ";
	}
	

}
